import React from "react";
import { useLocation } from "react-router-dom";
import { Typography, Card, CardContent, Container, Grid } from "@material-ui/core";

const DiseaseInfo = () => {
  const location = useLocation();
  const { disease } = location.state || {};

  if (!disease) {
    return <Typography variant="h5" align="center">No Disease Data Available</Typography>;
  }

  return (
    <Container maxWidth="md" style={{ marginTop: "20px" }}>
      {/* Disease Title */}
      <Typography variant="h4" align="center" gutterBottom>
        {disease.common_name || "Unknown Disease"}
      </Typography>

      <Grid container spacing={3}>
        {/* Description Section */}
        {disease.description && (
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom>
              Description
            </Typography>
            {disease.description.map((desc, index) => (
              <Card key={index} style={{ marginBottom: "10px" }}>
                <CardContent>
                  <Typography variant="h6">{desc.subtitle}</Typography>
                  <Typography variant="body1">{desc.description}</Typography>
                </CardContent>
              </Card>
            ))}
          </Grid>
        )}

        {/* Solution Section */}
        {disease.solution && (
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom>
              Solution
            </Typography>
            {disease.solution.map((sol, index) => (
              <Card key={index} style={{ marginBottom: "10px" }}>
                <CardContent>
                  <Typography variant="h6">{sol.subtitle}</Typography>
                  <Typography variant="body1" style={{ whiteSpace: "pre-line" }}>
                    {sol.description}
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default DiseaseInfo;
