import React from 'react'
import { useParams } from 'react-router-dom'
import { useState,useEffect } from 'react';
import { Typography } from '@material-ui/core';
const Info = () => {
    const[diseaseInfo,setDiseaseInfo]=useState(null);
    const {prediction}=useParams()
    useEffect(() => {
        fetch(`http://127.0.0.1:5000/get_disease_details?prediction=${prediction}`, {
            method: 'GET'
        })
        .then(response => response.json())
        .then(data => {
            setDiseaseInfo(data);
        })
        .catch(error => {
            console.error("Error fetching disease details:", error);
        });
    }, [prediction]);  
    
  return (
    <div>
      <Typography>{diseaseInfo.Title}</Typography>
      <Typography>{diseaseInfo.Summary}</Typography>
      <Typography>{diseaseInfo.Symptoms}</Typography>
      <Typography>{diseaseInfo.Treatment}</Typography>
    </div>
  )
}

export default Info
