import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.min.css";
import axios from "axios";
import { Typography } from "@material-ui/core";

const API_KEY = "sk-mrWl67e1927ba94389380";
const perenual_url = `https://perenual.com/api/pest-disease-list?key=${API_KEY}`;

const SwiperCarousel = () => {
  const [diseases, setDiseases] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDiseases = async () => {
      try {
        const response = await axios.get(perenual_url);
        console.log(response.data);
        setDiseases(response.data.data);
      } catch (error) {
        console.log("Error fetching diseases:", error);
      }
    };
    fetchDiseases();
  }, []);

  const handleClick = (disease) => {
    navigate(`/disease-info`, { state: { disease } });
  };

  return (
    <div style={{ width: "90%", margin: "auto", paddingTop: "20px" }}>
      {diseases.length > 0 ? (
        <Swiper
          spaceBetween={20}
          slidesPerView={3}
          autoplay={{ delay: 3000 }}
          navigation
          pagination={{ clickable: true }}
          breakpoints={{
            320: { slidesPerView: 1 },
            640: { slidesPerView: 2 },
            1024: { slidesPerView: 3 },
          }}
        >
          {diseases.map((disease) => (
            <SwiperSlide key={disease.id} onClick={() => handleClick(disease)}>
              <div style={{ textAlign: "center", cursor: "pointer" }}>
                <img
                  src={disease.images?.[0]?.medium_url}
                  alt={disease.common_name}
                  style={{
                    width: "100%",
                    height: "250px",
                    objectFit: "cover",
                    borderRadius: "10px",
                  }}
                />
                <Typography variant="h6">{disease.common_name}</Typography>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      ) : (
        <Typography variant="h6" align="center">
          No disease data available
        </Typography>
      )}
    </div>
  );
};

export default SwiperCarousel;
