import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import AliceCarousel from 'react-alice-carousel'
import { makeStyles,Typography,Link } from '@material-ui/core'
const API_KEY="sk-mrWl67e1927ba94389380"
const perenual_url=`https://perenual.com/api/pest-disease-list?key=${API_KEY}`
const useStyles=makeStyles(({
    carouselItem:{
        display:"flex",
        flexDirection:"column",
        alignItems:"center",
        textTransform:"uppercase",
        cursor:"pointer",
        color:"white"
    },
    carousel:{
        height:"50%",
        display:"flex",
        alignItems:"center"
    }
}))
const Carousel = () => {
    const classes=useStyles()
    const [diseases,setDiseases]=useState(null)
    const navigate=useNavigate();
    useEffect=(()=>{
     fetch(perenual_url,{
        method:"GET"
     }).then(response=>response.json())
     .then((data)=>{
        setDiseases(data.data)
     }).catch(error=>{
        console.error("Error fetching diseases: ",error)
     })
    },[])
    const handleClick = (disease) => {
        navigate(`/disease-info`, { state: { disease } });
      };
    const items=diseases.map((disease)=>{
  return (
    <Link className={classes.carouselItem} to="/disease-info" state={{disease}}>
     <img
    src={disease.images?.[0]?.medium_url}
    alt={disease.common_name}
    style={{
      width: "100%",
      height: "250px",
      objectFit: "cover",
      cursor: "pointer",
      borderRadius: "10px",
    }}
  />
  <Typography variant="h6" align="center">
    {disease.common_name}
  </Typography>
   </Link>
  )
})
return(
    <div className={classes.carousel}>
       <AliceCarousel
       mouseTracking
       infinite
       autoPlayInterval={1000}
       animationDuration={1500}
       disableDotsControls
       disableButtonsControls
       
       autoPlay
       items={items}
       />
    </div>
)
}

export default Carousel
