// export default Carousel
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AliceCarousel from "react-alice-carousel";
import { makeStyles, Typography } from "@material-ui/core";
import axios from "axios";

const API_KEY = "sk-mrWl67e1927ba94389380";
const perenual_url = `https://perenual.com/api/pest-disease-list?key=${API_KEY}`;

const useStyles = makeStyles(() => ({
  carouselContainer: {
    width: "100%",  
   // maxWidth: "900px",  // Restrict max width to keep layout intact
    margin: "0 auto",   // Center the carousel
    minHeight: "300px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    overflow: "hidden",
  },
  carouselWrapper: {
   // width: "100%",
    display: "flex",
    flexDirection: "row", // Ensures items are in a row
    justifyContent: "center",
    alignItems: "center",
  },
  carouselItem: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    cursor: "pointer",
    color: "black",
    textAlign: "center",
    //width: "250px",  // Fixed width to prevent layout issues
    margin: "0 10px", // Add some space between items
  },
  image: {
    width: "250px",
    height: "250px",
    objectFit: "cover",
    borderRadius: "10px",
  },
}));

const Carousel = () => {
  const classes = useStyles();
  const [diseases, setDiseases] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDiseases = async () => {
      try {
        const response = await axios.get(perenual_url);
        console.log("API Response:", response.data);
        setDiseases(response.data.data || []);
      } catch (error) {
        console.error("Error fetching diseases:", error);
      }
    };

    fetchDiseases();
  }, []);

  const items = diseases.map((disease) => (
    <div
      key={disease.id}
      className={classes.carouselItem}
      onClick={() => navigate(`/disease-info`, { state: { disease } })}
    >
      <img src={disease.images?.[0]?.medium_url} alt={disease.common_name} className={classes.image} />
      <Typography variant="h6">{disease.common_name}</Typography>
    </div>
  ));

  return (
    <div className={classes.carouselContainer}>
      <div className={classes.carouselWrapper}>
        {items.length > 0 ? (
          <AliceCarousel
            mouseTracking
            infinite
            autoPlayInterval={2000}
            animationDuration={1500}
            disableDotsControls={false}
            disableButtonsControls={false}
            autoPlay
            items={items}
            responsive={{
              0: { items: 1 },
              600: { items: 2 },
              1024: { items: 3 },
            }}
          />
        ) : (
          <Typography variant="h5">No disease data available</Typography>
        )}
      </div>
    </div>
  );
};

export default Carousel;
