import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Typography ,Box,Card,CardContent} from "@material-ui/core";

const Info = () => {
  const [diseaseInfo, setDiseaseInfo] = useState(null);
  const { prediction } = useParams();
 
  const extractSections = (text) => {
    let cleanText = text.replace(/\*\s*/g, "").trim();
    const regex = /(What it is:|Causes:|Symptoms:|Effects:|Treatment Plan:|Prevention Methods:)[\s\S]*?(?=(What it is:|Causes:|Symptoms:|Effects:|Treatment Plan:|Prevention Methods:|$))/g;
    const matches = [...cleanText.matchAll(regex)];
    //console.log("Matches Found:", matches);
    const sectionData = {};
    matches.forEach((match) => {
      const key = match[1].replace(":", "").trim();
      sectionData[key] = match[0].replace(match[1], "").trim();
    });
    console.log("Extracted Sections:", sectionData); 
    return sectionData;
  };
  useEffect(() => {
    const text = `*What it is:* A fast-spreading and highly destructive disease affecting tomato (and potato) plants.
*Causes:* Caused by the water mold Phytophthora infestans. It thrives in cool, wet, and humid weather. Spores spread by wind and rain splash.
*Symptoms:*
  * *Leaves:* Dark, water-soaked spots, often appearing greasy, sometimes with white fuzzy mold on the underside in humid conditions.
  * *Stems:* Dark, elongated lesions.
  * *Fruit:* Greasy-looking, firm, brown blotches that can rapidly enlarge and rot.
*Effects on the plant:* Rapidly kills leaves, stems, and fruit. Can quickly destroy entire plants and spread through a crop, leading to total loss. Makes fruit inedible.
*Treatment Plan:*
  * Difficult once established. Act immediately.
  * Remove and destroy (do not compost) infected plant parts or whole plants if severely affected.
  * Apply fungicides (copper-based or specific synthetic fungicides labelled for late blight) preventatively or at the very first sign, following label instructions. Repeat applications are usually necessary.
*Prevention Methods:*
  * Plant disease-resistant varieties.
  * Ensure good air circulation (space plants well, prune lower leaves).
  * Water at the base of plants (avoid overhead watering).
  * Mulch around plants.
  * Rotate crops (avoid planting tomatoes/potatoes in the same spot for 3-4 years).
  * Monitor weather; be vigilant during cool, wet periods.
  * Inspect plants frequently, especially after wet weather.
  * Control volunteer tomato/potato plants and related weeds.
  * Remove and destroy all plant debris at the end of the season.
`
   
    //if (!prediction) return;  // Prevents fetching if prediction is undefined
    const extract=extractSections(text)
    setDiseaseInfo(extract)
    // fetch(`http://127.0.0.1:5000/diseaseinfo?prediction=${prediction}`)
    //   .then(response => {
    //     if (!response.ok) {
    //       throw new Error("Failed to fetch disease details");
    //     }
    //     return response.json();
    //   })
    //   .then(data => {
    //     const extracted=extractSections(data.info)
    //     setDiseaseInfo(extracted);
    //   })
    //   .catch(error => {
    //     console.error("Error fetching disease details:", error);
    //   });
  }, []); 

  if (!diseaseInfo) {
    return <Typography>Loading...</Typography>;
  }

  return (
   <Box style={{marginTop:"4px",display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
    <Typography variant="h4" align="center" style={{fontFamily:"'Poppins', sans-serif",fontWeight:'bold',color:"#2E7D32" ,marginBottom:"30px"}}>ðŸŒ± Plant Disease Information</Typography>
    {Object.entries(diseaseInfo).map(([title,content],index)=>(
    <Card
    key={index}
    style={{
      backgroundColor: "#E8F5E9", // Light green background
      boxShadow: "0px 4px 12px rgba(0, 100, 0, 0.2)", // Greenish shadow effect
      borderRadius: 3, // Rounded corners
      marginBottom: 3, // Spacing between cards
    }}
  >
    <CardContent style={{ padding: 3, textAlign: "left" }}>
      <Typography variant="h6" sx={{ fontWeight: "bold", fontFamily: "'Poppins', sans-serif", color: "#1B5E20" }}>
        {title}
      </Typography>
      <Typography variant="body1" sx={{ fontSize: "15px", fontFamily: "'Poppins', sans-serif", lineHeight: 1.6 }}>
        {content}
      </Typography>
    </CardContent>
  </Card>
    ))};
   </Box>
  );
};

export default Info;
