import React from "react";
import { useLocation } from "react-router-dom";
import { Typography, Card, CardContent, Container, Grid ,makeStyles} from "@material-ui/core";
const useStyles = makeStyles({
  card: {
    backgroundColor: "#F1F8E9",
    boxShadow: "0px 6px 15px rgba(0, 100, 0, 0.2)",
    borderRadius: "12px",
    padding: "16px",
    transition: "transform 0.3s ease-in-out",
    "&:hover": { transform: "scale(1.05)" }, 
  },
  title: {
    fontWeight: "bold",
    fontFamily: "'Poppins', sans-serif",
    color: "#2E7D32",
    marginBottom:"10px"
  },
  content: {
    fontSize: "15px",
    fontFamily: "'Poppins', sans-serif",
    lineHeight: 1.6,
    color: "#333",
  },
});
const DiseaseInfo = () => {
   const classes=useStyles()
  const location = useLocation();
  const { disease } = location.state || {};

  if (!disease) {
    return <Typography variant="h5" align="center">No Disease Data Available</Typography>;
  }

  return (
    <Container maxWidth="md" style={{ marginTop: "20px" }}>
      {/* Disease Title */}
      <Typography variant="h4" align="center" gutterBottom>
        {disease.common_name || "Unknown Disease"}
      </Typography>

      <Grid container spacing={3}>
        {/* Description Section */}
        {disease.description && (
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom className={classes.title}>
              Description
            </Typography>
            {disease.description.map((desc, index) => (
              <Card key={index} style={{ marginBottom: "10px" }} className={classes.card}>
                <CardContent >
                  <Typography variant="h6" className={classes.content}>{desc.subtitle}</Typography>
                  <Typography variant="body1" className={classes.content}>{desc.description}</Typography>
                </CardContent>
              </Card>
            ))}
          </Grid>
        )}

        {/* Solution Section */}
        {disease.solution && (
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom>
              Solution
            </Typography>
            {disease.solution.map((sol, index) => (
              <Card key={index} style={{ marginBottom: "10px" }}>
                <CardContent>
                  <Typography variant="h6">{sol.subtitle}</Typography>
                  <Typography variant="body1" style={{ whiteSpace: "pre-line" }}>
                    {sol.description}
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default DiseaseInfo;
