import logo from './logo.svg';
import './App.css';
import Navbar from './components/Navbar'
import Banner from './components/Banner'
import {Dialog,DialogTitle,DialogContent,DialogActions,Button,makeStyles} from '@material-ui/core'
import { useState } from 'react';
const useStyles=makeStyles(()=>({
  appContainer: {
    backgroundColor: "#d9f2d9",
    minHeight: "100vh",
    paddingBottom: "20px",
  },
  modalContent: {
    textAlign: "center",
    padding: "30px",
    backgroundColor: "#e6ffe6", 
  },
  uploadButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
    "&:hover": {
      backgroundColor: "#388E3C",
    },
  },
  dialogPaper: {
    minWidth: "400px", 
    borderRadius: "12px",
  }
}));

function App() {
  const [open,setOpen]=useState(false);
  const classes=useStyles();
  return (
    <div className={classes.appContainer}>
      <Navbar setOpen={setOpen}/>
      <Banner />
      <Dialog open={open} onClose={() => setOpen(false)} classes={{ paper: classes.dialogPaper }}>
        <DialogTitle style={{ backgroundColor: "#c8e6c9", textAlign: "center" }}>
          ðŸŒ± Upload an Image
        </DialogTitle>
        <DialogContent className={classes.modalContent}>
          <input type="file" accept="image/*" style={{ marginBottom: "10px" }} />
          <br />
          <Button className={classes.uploadButton}>Upload</Button>
        </DialogContent>
        <DialogActions style={{ backgroundColor: "#c8e6c9" }}>
          <Button onClick={() => setOpen(false)} color="secondary">Close</Button>
          <Button onClick={() => alert("Prediction in progress...")} color="primary">
            Predict
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default App;
