import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import AliceCarousel from 'react-alice-carousel'
import { makeStyles,Typography} from '@material-ui/core'
import Link from 'react-router-dom'
import axios from  'axios'
const API_KEY="sk-mrWl67e1927ba94389380"
const perenual_url=`https://perenual.com/api/pest-disease-list?key=${API_KEY}`
const useStyles=makeStyles(({
    carouselItem:{
        display:"flex",
        flexDirection:"column",
        alignItems:"center",
        textTransform:"uppercase",
        cursor:"pointer",
        color:"white",
        height:"500px",
    },
    carousel:{
        minHeight:"300px",
        display:"flex",
        alignItems:"center",
        width: "100%",
    }
}))
const Carousel = () => {
    const classes=useStyles()
    const [diseases,setDiseases]=useState([])
    const navigate=useNavigate();
    useEffect(()=>{
        const fetchDiseases=async()=>{
        try{
            const response=await axios.get(perenual_url)
            console.log(response)
            setDiseases(response.data.data)
            console.log(diseases)
        }
        catch(error){
        console.log(error)
        }
        }
    fetchDiseases()
    },[])
    const handleClick = (disease) => {
        navigate(`/disease-info`, { state: { disease } });
      };
      const items =
      diseases.length > 0
        ? diseases.map((disease) => (
            <div
              key={disease.id}
              className={classes.carouselItem}
              onClick={() => handleClick(disease)}
              style={{ cursor: "pointer" }}
            >
              <img
                src={disease.images?.[0]?.medium_url}
                alt={disease.common_name}
                style={{
                  width: "200px",
                  height: "250px",
                  objectFit: "cover",
                  cursor: "pointer",
                  borderRadius: "10px",
                }}
              />
              <Typography variant="h6" align="center">
                {disease.common_name}
              </Typography>
            </div>
          ))
        : [<Typography key="no-data" align="center">No disease data available.</Typography>];
  
return(
    <div className={classes.carousel}>
       <AliceCarousel
       mouseTracking
       infinite
       autoPlayInterval={1000}
       animationDuration={1500}
       disableDotsControls
       disableButtonsControls
       autoPlay
       items={items}
       />
    </div>
)
}

export default Carousel
