import logo from './logo.svg';
import './App.css';
import Navbar from './components/Navbar'
import Banner from './components/Banner'
import {Dialog,DialogTitle,DialogContent,DialogActions,Button,makeStyles} from '@material-ui/core'
import { useState } from 'react';
const useStyles=makeStyles(()=>({
  appContainer: {
    backgroundColor: "#d9f2d9",
    minHeight: "100vh",
    paddingBottom: "20px",
  },
  modalContent: {
    textAlign: "center",
    padding: "30px",
    backgroundColor: "#e6ffe6", 
  },
  uploadButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
    "&:hover": {
      backgroundColor: "#388E3C",
    },
  },
  uploadLabel: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "12px 20px",
    borderRadius: "8px",
    cursor: "pointer",
    display: "inline-block",
    fontSize: "16px",
    fontWeight: "bold",
    "&:hover": {
      backgroundColor: "#388E3C",
    },
  },
  fileName: {
    marginTop: "10px",
    fontSize: "14px",
    fontWeight: "bold",
    color: "#2e7d32",
  },
  hiddenInput: {
    display: "none",
  },
  dialogPaper: {
    minWidth: "400px", 
    borderRadius: "12px",
  }
}));

function App() {
  const [open,setOpen]=useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const classes=useStyles();
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
    }
  };
  return (
    <div className={classes.appContainer}>
      <Navbar setOpen={setOpen}/>
      <Banner />
      <Dialog open={open} onClose={() => setOpen(false)} classes={{ paper: classes.dialogPaper }}>
        <DialogTitle style={{ backgroundColor: "#c8e6c9", textAlign: "center" }}>
          ðŸŒ± Upload an Image
        </DialogTitle>
        <DialogContent className={classes.modalContent}>
        <label htmlFor="file-upload" className={classes.uploadLabel}>
            Choose an Image
          </label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            className={classes.hiddenInput}
            onChange={handleImageChange}
          />
           {selectedFile && <div className={classes.fileName}>ðŸ“‚ {selectedFile.name}</div>}
          <br />
          <Button className={classes.uploadButton}>Upload</Button>
        </DialogContent>
        <DialogActions style={{ backgroundColor: "#c8e6c9" }}>
          <Button onClick={() => setOpen(false)} color="secondary">Close</Button>
          <Button onClick={() => alert("Prediction in progress...")} color="primary">
            Predict
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default App;
