// export default Carousel
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AliceCarousel from 'react-alice-carousel';
import { makeStyles, Typography } from '@material-ui/core';
import axios from 'axios';

const API_KEY = "sk-mrWl67e1927ba94389380";
const perenual_url = `https://perenual.com/api/pest-disease-list?key=${API_KEY}`;

const useStyles = makeStyles(() => ({
  carousel: {
    width: "100%",
    minHeight: "300px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    overflow: "hidden",
  },
  carouselItem: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    cursor: "pointer",
    color: "black",
  },
}));

const Carousel = () => {
  const classes = useStyles();
  const [diseases, setDiseases] = useState([]);
  const [items, setItems] = useState([]); // Store the mapped carousel items
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDiseases = async () => {
      try {
        const response = await axios.get(perenual_url);
        console.log("API Response:", response.data);
        setDiseases(response.data.data || []); // Ensure it's an array
      } catch (error) {
        console.error("Error fetching diseases:", error);
      }
    };

    fetchDiseases();
  }, []);

  useEffect(() => {
    if (diseases.length > 0) {
      const newItems = diseases.map((disease) => (
        <div
          key={disease.id}
          className={classes.carouselItem}
          onClick={() => navigate(`/disease-info`, { state: { disease } })}
          style={{ cursor: "pointer" }}
        >
          <img
            src={disease.images?.[0]?.medium_url}
            alt={disease.common_name}
            style={{
              width: "200px",
              height: "250px",
              objectFit: "cover",
              borderRadius: "10px",
            }}
          />
          <Typography variant="h6" align="center">
            {disease.common_name}
          </Typography>
        </div>
      ));
      setItems(newItems);
    }
  }, [diseases]); // Runs only when diseases change

  return (
    <div className={classes.carousel}>
      {items.length > 1 ? ( // Only show carousel if more than one item exists
        <AliceCarousel
          mouseTracking
          infinite
          autoPlayInterval={2000}
          animationDuration={1500}
          items={items}
          disableDotsControls={false} // Show dots
          disableButtonsControls={false} // Show navigation
          autoPlay
        />
      ) : (
        <Typography variant="h5" align="center">No disease data available</Typography>
      )}
    </div>
  );
};

export default Carousel;
